from __future__ import division


def summation(a, b):
    return "Sum of {0} and {1} is {2}".format(a, b, int(a) + int(b))


def subtraction(a, b):
    return "Difference of {0} and {1} is {2}".format(a, b, int(a) - int(b))


def multiplication(a, b):
    return "Product of {0} and {1} is {2}".format(a, b, int(a) * int(b))


def division(a, b):
    return "Quotient of {0} and {1} is {2}".format(a, b, int(a) / int(b))
