import os
import subprocess
import warnings
from setuptools import setup, find_packages

################################################################################
def readme():
	""" Return the README text.
	"""
	with open('README.rst') as fh:
		return fh.read()

################################################################################
def get_version():
	""" Gets the current version of the package.
	"""
	# Write the version to a Python file.
	# This make it easy to query the packge's version from Python,
	# and also works to determine the version when not in a git checkout.
	version_py = os.path.join(os.path.dirname(__file__), 'kur', 'version.py')

	# Try to use git first.
	try:
		version_git = subprocess.check_output(
			["git", "describe", "--abbrev=0"]
		).decode('utf-8').rstrip()
	except (FileNotFoundError, subprocess.CalledProcessError):
		# Oops. Just get it from ther version file.
		version_git = None
		try:
			with open(version_py, 'r') as fh:
				for line in fh:
					if line.startswith('__version__'):
						version_git = line.split('=')[-1].strip(
							).replace('"', '')
						break
		except FileNotFoundError:
			pass
		if version_git is None:
			warnings.warn('Failed to find versioning information. Using a '
				'development version.')
			version_git = '0.0.1'

	# Update the version file.
	with open(version_py, 'w') as fh:
		fh.write('''"""
Copyright 2016 Deepgram

Licensed under the Apache License, Version 2.0 (the "License");
you may not use this file except in compliance with the License.
You may obtain a copy of the License at

   http://www.apache.org/licenses/LICENSE-2.0

Unless required by applicable law or agreed to in writing, software
distributed under the License is distributed on an "AS IS" BASIS,
WITHOUT WARRANTIES OR CONDITIONS OF ANY KIND, either express or implied.
See the License for the specific language governing permissions and
limitations under the License.
"""

# Do not edit this file, pipeline versioning is governed by git tags
__version__ = "{ver}"

#### EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF
'''.format(ver=version_git))

	return version_git

################################################################################
setup(
	# Package information
	name='kur',
	version=get_version(),
	description='Descriptive deep learning',
	long_description=readme(),
	keywords='deep learning',
	classifiers=[
	],

	# Author information
    url='https://github.com/deepgram/kur',
	author='Adam Sypniewski',
	author_email='adam@deepgram.com',
	license='Apache Software License '
		'(http://www.apache.org/licenses/LICENSE-2.0)',

	# What is packaged here.
	packages=find_packages(),

	# What to include.
	package_data={
		'': ['*.txt', '*.rst', '*.md']
	},

	# Dependencies
	install_requires=[
		'pyyaml',
		'jinja2',
		'numpy',
		'requests',
		'tqdm',

		# Keras - the default backend
		'keras',
		'theano',
		'h5py'
	],
	dependency_links=[
	],

	# Testing
	test_suite='tests',
	tests_require=['pytest'],
	setup_requires=['pytest-runner'],

	entry_points={
		'console_scripts' : ['kur=kur.__main__:main']
	},

	zip_safe=False
)

#### EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF.EOF
