from unittest import TestCase
from Wammu.Utils import GetItemType


class UtilsTest(TestCase):
    def test_type(self):
        self.assertEqual(GetItemType('LastModified'), 'datetime')
        self.assertEqual(GetItemType('PictureID'), 'id')
